import React, {useState} from "react"
import {PRODUCTS} from '../../products'
import { BsSearch } from 'react-icons/bs';
import {Product} from './product'
import './searchShop.css';


export const Search = () => {
    this.state = {
        show: false
      };
    const [state, useState] = useState(false)
    const [products, setProducts] = useState(PRODUCTS);
    const [searchVal, setSearchVal] = useState("");
    function handleSearchClick() {
        if (searchVal === "") { setProducts(PRODUCTS); return; }
        const filterBySearch = PRODUCTS.filter((item) => {
            if (item.productName.toLowerCase()
                .includes(searchVal.toLowerCase())) { return item; }
        })
        setProducts(filterBySearch);
        this.setState({
            show: true
          });

    }
    // const mystyle = {
    //     marginLeft: "600px",
    //     marginTop: "20px",
    //     fontWeight: "700"
    // };

    
  
    return(
        <div className="container">
            <div className="searchbar">
                <input onChange={e => setSearchVal(e.target.value)} placeholder="Search Products" 
          name="product-search" 
          id="product-search">
                </input>
                <BsSearch onClick={handleSearchClick} className="bssearch"/>
            </div>
            <div className="products">
                
                {
                products.map((product) => {
                    if (this.state) {
                        <Product data={product} />
                    }
                
                })} 
  
            </div>
           <hr  style={{
                    color: '#000000',
                    backgroundcolor: '#000000',
                    height: .5,
                    bordercolor : '#000000'
}}/>
    
        </div>
    )
}
